import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './inscription.scss';

function Inscription() {
  const [formData, setFormData] = useState({
    nom: '',
    prenom: '',
    email: '',
    motDePasse: '',
    confirmMotDePasse: '',
  });

  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (formData.motDePasse !== formData.confirmMotDePasse) {
      setError('Les mots de passe ne correspondent pas.');
      setFormData({ ...formData, motDePasse: '', confirmMotDePasse: '' });
      return;
    }

    setError('');

    const payload = {
      nom: formData.nom,
      prenom: formData.prenom,
      email: formData.email,
      mdp: formData.motDePasse, // ✅ backend attend 'mdp'
    };

    try {
      const response = await fetch('http://localhost:3000/marieteam/signup', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });

      const result = await response.json();
      if (result.success) {
        setSuccessMessage(result.message);
        navigate('/login', {
          state: {
            success: 'Inscription réussie! Vous pouvez maintenant vous connecter.',
          },
        });
      } else {
        setError(result.message || 'Une erreur est survenue.');
      }
    } catch (error) {
      setError('Une erreur est survenue. Veuillez réessayer.');
    }
  };

  return (
    <div className="inscription">
      <h2 className="inscription__text">Créer un compte</h2>
      <form onSubmit={handleSubmit}>
        <div className="inscription__form-group">
          <label htmlFor="nom">Nom :</label>
          <input
            type="text"
            id="nom"
            name="nom"
            value={formData.nom}
            onChange={handleChange}
            required
          />
        </div>

        <div className="inscription__form-group">
          <label htmlFor="prenom">Prénom :</label>
          <input
            type="text"
            id="prenom"
            name="prenom"
            value={formData.prenom}
            onChange={handleChange}
            required
          />
        </div>

        <div className="inscription__form-group">
          <label htmlFor="email">Email :</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="inscription__form-group">
          <label htmlFor="motDePasse">Mot de passe :</label>
          <input
            type="password"
            id="motDePasse"
            name="motDePasse"
            value={formData.motDePasse}
            onChange={handleChange}
            required
          />
        </div>

        <div className="inscription__form-group">
          <label htmlFor="confirmMotDePasse">Confirmer le mot de passe :</label>
          <input
            type="password"
            id="confirmMotDePasse"
            name="confirmMotDePasse"
            value={formData.confirmMotDePasse}
            onChange={handleChange}
            required
          />
        </div>

        {error && <p className="inscription__error-message">{error}</p>}
        {successMessage && <p className="inscription__success-message">{successMessage}</p>}

        <button type="submit" className="inscription__button">
          S'inscrire
        </button>
      </form>
    </div>
  );
}

export default Inscription;
