import React, { useEffect, useState } from "react";
import "./Reservations.scss";

function Reservations() {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const userId = localStorage.getItem("userId");

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const res = await fetch(`http://localhost:3000/reservations/user/${userId}`);
        const data = await res.json();
        setReservations(data);
      } catch (error) {
        console.error("Erreur lors du chargement des réservations :", error);
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      fetchReservations();
    }
  }, [userId]);

  if (loading) return <p>Chargement des réservations...</p>;

  return (
    <div className="reservations">
      <h1 className="reservations__title">Mes réservations</h1>

      {reservations.length === 0 ? (
        <p className="reservations__empty">Aucune réservation trouvée.</p>
      ) : (
        <ul className="reservations__list">
          {reservations.map((res) => (
            <li key={res.Id_Reservation} className="reservations__item">
              <p><strong>Numéro :</strong> {res.num}</p>
              <p><strong>Date :</strong> {res.traversee?.dateT}</p>
              <p><strong>Heure :</strong> {res.traversee?.heure}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default Reservations;
